import React, { useRef, useEffect } from "react";
import ForceGraph2D from 'react-force-graph-2d';
import { graphData } from "./data/graphData";

function App() {
  const graphRef = useRef();


  return (
    <div style={{width:"100vw", height: "100vh", padding: "50px" }}>
      <ForceGraph2D
        width={1000}
        height={700}
        backgroundColor="#060816"
        ref={graphRef}
        graphData={graphData}
        nodeAutoColorBy="group"
        nodeLabel="id"
        linkDirectionalParticles={2}
        linkDirectionalParticleSpeed={d => d.value * 0.001}
        linkWidth={d => d.value * 0.1}
        linkColor={link => link.color || "#ffffff"}
        linkDirectionalArrowLength={6} // Arrow length in pixels
        linkDirectionalArrowRelPos={1} // Arrow position (0 = start, 1 = end, 0.5 = middle)
        onNodeClick={node => alert(`Clicked node: ${node.id}`)}
        d3Force="charge"
        d3VelocityDecay={0.8}
        nodeCanvasObject={(node, ctx, globalScale) => {
          const size = 10; // Node size
          ctx.save();
          ctx.translate(node.x, node.y);

          // Custom shape logic
          if (node.group === 1) {
            // Draw a circle
            ctx.beginPath();
            ctx.arc(0, 0, size, 0, 2 * Math.PI, false);
            ctx.fillStyle = node.color || "white";
            ctx.fill();
          } else if (node.group === 2) {
            // Draw a square
            ctx.fillStyle = node.color || "white";
            ctx.fillRect(-size, -size, size * 2, size * 2);
          } else {
            // Draw a triangle
            ctx.beginPath();
            ctx.moveTo(0, -size);
            ctx.lineTo(size, size);
            ctx.lineTo(-size, size);
            ctx.closePath();
            ctx.fillStyle = node.color || "white";
            ctx.fill();
          }

          // Add node label
          const fontSize = 12 / globalScale;
          ctx.font = `${fontSize}px Sans-Serif`;
          ctx.textAlign = "center";
          ctx.textBaseline = "middle";
          ctx.fillStyle = "black";
          ctx.fillText(node.id, 0, size + 10); // Label below the node

          ctx.restore();
        }}
      />
    </div>
  );
}

export default App;
