import React, { useRef, useEffect, useState, useMemo } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { graphData as originalGraphData } from "./data/graphData";
import "./app.css";
import AddressList from "./table";

function App() {
  const graphRef = useRef();

  const [visibleGroups, setVisibleGroups] = useState([1, 2]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [filteredNodes, setFilteredNodes] = useState(originalGraphData.nodes);

    // Utility: Get node ID whether object or string
  const getNodeId = (node) => (typeof node === "object" ? node.id : node);
  // Configure force graph link distance
  useEffect(() => {
    if (graphRef.current) {
      graphRef.current.d3Force("link").distance((link) => link.customLength || 50);
    }
  }, []);

  // Update filtered nodes when groups change
  useEffect(() => {
    setFilteredNodes(
      originalGraphData.nodes.filter((node) => visibleGroups.includes(node.group))
    );
  }, [visibleGroups]);

  // Filter visible links
  const filteredLinks = useMemo(() => {
    const visibleNodeIds = filteredNodes.filter(n => n.visible).map(n => n.id);
    return originalGraphData.links.filter(
      (link) =>
        visibleNodeIds.includes(getNodeId(link.source)) &&
        visibleNodeIds.includes(getNodeId(link.target))
    );
  }, [filteredNodes]);

  // Final graph data
  const filteredGraphData = useMemo(() => {
    return {
      nodes: filteredNodes.filter((node) => node.visible),
      links: filteredLinks
    };
  }, [filteredNodes, filteredLinks]);

  // Re-run simulation when data changes
  useEffect(() => {
    if (graphRef.current) {
      graphRef.current.d3ReheatSimulation();
      graphRef.current.zoomToFit(800, 80); // smooth transition
    }
  }, [filteredGraphData, visibleGroups]);

  // Toggle group visibility
  const toggleGroup = (group) => {
    setVisibleGroups((groups) =>
      groups.includes(group) ? groups.filter((g) => g !== group) : [...groups, group]
    );
  };

  // Node click handler
  const handleClickNode = (node) => {
    setSelectedNode(node.id);
    console.log(`Selected node: ${node.id}`);
  };

  // Toggle individual node visibility
  const onToggleVisible = (id) => {
    setFilteredNodes((prevNodes) =>
      prevNodes.map((node) =>
        node.id === id ? { ...node, visible: !node.visible } : node
      )
    );
  };



  // Custom Node Renderer
  const renderNode = (node, ctx) => {
    const radius = 12;
    if (!isFinite(node.x) || !isFinite(node.y)) return;

    // Outer ring
    ctx.beginPath();
    ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
    ctx.fillStyle = "#ffffff";
    ctx.fill();

    // Inner gradient
    const innerRadius = radius - 1;
    const gradient = ctx.createRadialGradient(
      node.x, node.y, innerRadius / 4, node.x, node.y, innerRadius
    );
    gradient.addColorStop(0, node.group === 1 ? "#ffcc00" : "#95D5E9");
    gradient.addColorStop(1, "#000000");

    ctx.beginPath();
    ctx.arc(node.x, node.y, innerRadius, 0, 2 * Math.PI, false);
    ctx.fillStyle = gradient;
    ctx.fill();
  };

  return (
    <div className="app-container">
      <div className="controls">
        {[1, 2].map((group) => (
          <button key={group} onClick={() => toggleGroup(group)}>
            {visibleGroups.includes(group) ? `Hide Group ${group}` : `Show Group ${group}`}
          </button>
        ))}
        <button>Add a node</button>
      </div>

      <AddressList
        nodes={filteredNodes}
        selectedId={selectedNode}
        onToggleVisible={onToggleVisible}
      />

      <ForceGraph2D
        ref={graphRef}
        graphData={filteredGraphData}
        backgroundColor="#091024"
        nodeAutoColorBy="group"
        nodeLabel="name"
        linkDirectionalParticles={2}
        linkDirectionalParticleSpeed={(d) => d.value * 0.001}
        linkWidth={(d) => d.value * 0.1}
        linkColor={() => "#ffffff"}
        linkDirectionalArrowLength={8}
        linkDirectionalArrowRelPos={0.8}
        onNodeClick={handleClickNode}
        d3Force="charge"
        d3VelocityDecay={0.5}
        nodeCanvasObject={renderNode}
        minZoom={0.5}   // Minimum zoom level
        maxZoom={5}     // Maximum zoom level
      />
    </div>
  );
}

export default App;
