import React, { useState } from "react";
import { FiEye, FiEyeOff, FiMaximize2 } from "react-icons/fi";
import "./table.css";

const addresses = [
  { id: 8, name: "OKX Cold Wallet", share: "1.32%", visible: false },
  { id: 9, name: "Binance 14", share: "1.06%", visible: false },
  { id: 10, name: "0x98ad...ba9d", share: "0.94%", visible: true },
  { id: 11, name: "Okx 14", share: "0.77%", visible: false },
  { id: 12, name: "Okx 15", share: "0.77%", visible: false },
  { id: 13, name: "Okx 13", share: "0.77%", visible: false },
  { id: 14, name: "Tether Inventory", share: "0.68%", visible: false },
  { id: 15, name: "Okx 10", share: "0.64%", visible: false },
  { id: 16, name: "Okx 12", share: "0.64%", visible: false },
  { id: 17, name: "Okx 16", share: "0.64%", visible: false },
  { id: 18, name: "Okx 8", share: "0.64%", visible: false },
];


const AddressList = ({a}) => {
  const [search, setSearch] = useState("");

  const filtered = addresses.filter((item) =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="address-list">
      <div className="address-list-header">
        <h2>Address List</h2>
        <FiMaximize2 className="icon-btn" />
      </div>

      {/* Search */}
      <div className="search-wrapper">
        <input
          type="text"
          placeholder="Search"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <span className="search-icon">üîç</span>
      </div>

      {/* Filters */}
      <div className="filters">
        <label>
          <input type="checkbox" /> Show contracts
        </label>
        <label>
          <input type="checkbox" /> Show exchanges
        </label>
      </div>

      {/* Header row */}
      <div className="table-header">
        <span>Address</span>
        <span>Share of USDT supply</span>
      </div>

      {/* Rows */}
      <div className="address-rows">
        {filtered.map((item) => (
          <div
            className={`address-row ${
              selectedId === item.id ? "selected" : ""
            }`}
            key={item.id}
          >
            <div className="address-info">
              {item.visible ? (
                <FiEye className="icon visible" />
              ) : (
                <FiEyeOff className="icon hidden" />
              )}
              <span className="address-id">#{item.id}</span>
              <span className="address-name">{item.name}</span>
            </div>
            <span className="address-share">{item.share}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddressList;
