import React, { useRef, useEffect, useState, useMemo } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { graphData, graphData as originalGraphData } from "./data/graphData";
import "./app.css";

function App() {
  const graphRef = useRef();
  const [visibleGroups, setVisibleGroups] = useState([1,2]); // adjust group numbers as needed

  useEffect(() => {
    if (graphRef.current) {
      // Set link distance to 200 pixels (customize as needed)
      graphRef.current
        .d3Force("link")
        .distance((link) => link.customLength || 50);
    }
  }, []);

  const filteredNodes = useMemo(() => {
    return originalGraphData.nodes.filter(node =>
      visibleGroups.includes(node.group)
    );
  }, [visibleGroups]);

  const filteredLinks = useMemo(() => {
    return originalGraphData.links.filter(link =>
      visibleGroups.includes(link.source.group) && visibleGroups.includes(link.target.group)
    );
  }, [visibleGroups, originalGraphData]);

  const filteredGraphData = useMemo(() => {
    return { nodes: filteredNodes, links: filteredLinks };
  }, [filteredNodes, filteredLinks])

  const toggleGroup = (group) => {
    setVisibleGroups(groups =>
      groups.includes(group)
        ? groups.filter(g => g !== group)
        : [...groups, group]
    );
  }

  return (
    <div style={{ width: "100vw", height: "100vh", alignContent: "center", justifyItems: "center" }}>
      <div style={{ margin: "10px" }}>
        {[1, 2, 3].map(group => (
          <button key={group} onClick={() => toggleGroup(group)}>
            {visibleGroups.includes(group) ? `Hide Group ${group}` : `Show Group ${group}`}
          </button>
        ))}
      </div>
      <ForceGraph2D
        width={1000}
        height={700}
        backgroundColor="#091024"
        ref={graphRef}
        graphData={filteredGraphData}
        nodeAutoColorBy="group"
        nodeLabel="id"
        linkDirectionalParticles={2}
        linkDirectionalParticleSpeed={(d) => d.value * 0.001}
        linkWidth={(d) => d.value * 0.1}
        linkColor={(link) => link.color || "#ffffff"}
        linkDirectionalArrowLength={8} // Arrow length in pixels
        linkDirectionalArrowRelPos={0.8} // Arrow position (0 = start, 1 = end, 0.5 = middle)
        onNodeClick={(node) => alert(`Clicked node: ${node.id}`)}
        d3Force="charge"
        d3VelocityDecay={0.4}
        nodeCanvasObject={(node, ctx, globalScale) => {
          const radius = 12;

          // Skip rendering if coordinates aren't ready yet
          if (!isFinite(node.x) || !isFinite(node.y)) return;

          // Draw outer white ring
          ctx.beginPath();
          ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
          ctx.fillStyle = "#ffffff";
          ctx.fill();

          // Draw gradient inner circle
          const innerRadius = radius - 1;
          const gradient = ctx.createRadialGradient(
            node.x,
            node.y,
            innerRadius / 4,
            node.x,
            node.y,
            innerRadius
          );
          gradient.addColorStop(0, "#c4b134");
          gradient.addColorStop(1, "#000000");

          ctx.beginPath();
          ctx.arc(node.x, node.y, innerRadius, 0, 2 * Math.PI, false);
          ctx.fillStyle = gradient;

          ctx.fill();
        }}
      />
    </div>
  );
}

export default App;
