import React, { useRef, useEffect } from "react";
import ForceGraph2D from 'react-force-graph-2d';
import { graphData } from "./data/graphData";

function App() {
  const graphRef = useRef();


  return (
    <div style={{width:"100vw", height: "100vh", padding: "50px" }}>
      <ForceGraph2D
        width={1000}
        height={700}
        backgroundColor="#060816"
        ref={graphRef}
        graphData={graphData}
        nodeAutoColorBy="group"
        nodeLabel="id"
        linkDirectionalParticles={2}
        linkDirectionalParticleSpeed={d => d.value * 0.001}
        linkWidth={d => d.value * 0.1}
        linkColor={link => link.color || "#ffffff"}
        linkDirectionalArrowLength={6} // Arrow length in pixels
        linkDirectionalArrowRelPos={1} // Arrow position (0 = start, 1 = end, 0.5 = middle)
        onNodeClick={node => alert(`Clicked node: ${node.id}`)}
        d3Force="charge"
        d3VelocityDecay={0.8}
        // nodeCanvasObject={(node, ctx, globalScale) => {
        //   const radius = 12;
        //   const gradient = ctx.createRadialGradient(
        //     node.x, node.y, radius / 4,
        //     node.x, node.y, radius
        //   );
        //   gradient.addColorStop(0, '#c4b134'); // inner color
        //   gradient.addColorStop(1, '#000000'); // outer color

        //   // Draw circle with white border
        //   ctx.beginPath();
        //   ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
        //   ctx.fillStyle = gradient;
        //   ctx.fill();
        //   ctx.lineWidth = 1;
        //   ctx.strokeStyle = '#ffffff';
        //   ctx.stroke();
        // }}
        // nodePointerAreaPaint={(node, color, ctx) => {
        //   const radius = 12;
        //   ctx.fillStyle = color;
        //   ctx.beginPath();
        //   ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
        //   ctx.fill();
        // }}
        nodeCanvasObject={(node, ctx, globalScale) => {
          const radius = 12;

          // Draw outer white border
          ctx.beginPath();
          ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
          ctx.fillStyle = '#ffffff';
          ctx.fill();

          // Draw smaller inner circle with gradient (leaves a white ring)
          const innerRadius = radius - 4;
          const gradient = ctx.createRadialGradient(
            node.x, node.y, innerRadius / 4,
            node.x, node.y, innerRadius
          );
          gradient.addColorStop(0, '#c4b134');
          gradient.addColorStop(1, '#000000');

          ctx.beginPath();
          ctx.arc(node.x, node.y, innerRadius, 0, 2 * Math.PI, false);
          ctx.fillStyle = gradient;
          ctx.fill();
        }}
      />
    </div>
  );
}

export default App;
